// Enrich each Apollo contact with additional data
const enrichedContacts = [];

for (const contact of $input.all()) {
  const contactData = contact.json;

  // Enrich with additional Apollo data
  const enrichedContact = {
    // Basic contact info
    apollo_contact_id: contactData.id,
    first_name: contactData.first_name,
    last_name: contactData.last_name,
    title: contactData.title,
    email: contactData.email,

    // Company information
    company_name: contactData.organization?.name,
    company_industry: contactData.organization?.industry,
    company_size: contactData.organization?.size,
    company_founded_year: contactData.organization?.founded_year,
    company_website: contactData.organization?.website,
    company_linkedin: contactData.organization?.linkedin_url,
    company_phone: contactData.organization?.phone,
    company_address: contactData.organization?.address,

    // Location data
    city: contactData.location?.city,
    state: contactData.location?.state,
    country: contactData.location?.country,

    // Additional Apollo fields
    page_retrieved: contactData.page_retrieved,
    contact_email_status: contactData.contact_email_status,

    // Raw Apollo data for feature engineering
    apollo_raw_data: contactData
  };

  enrichedContacts.push(enrichedContact);
}

return enrichedContacts;

// Merge Apollo and Instantly enrichment data
const mergedData = [];

for (const contact of $input.all()) {
  const apolloData = contact.json;
  const instantlyData = contact.instantly_enrichment || {};

  // Create merged contact with all enrichment data
  const mergedContact = {
    // Apollo data
    apollo_contact_id: apolloData.apollo_contact_id,
    first_name: apolloData.first_name,
    last_name: apolloData.last_name,
    title: apolloData.title,
    email: apolloData.email,
    company_name: apolloData.company_name,
    company_industry: apolloData.company_industry,
    company_size: apolloData.company_size,
    company_founded_year: apolloData.company_founded_year,
    company_website: apolloData.company_website,
    company_linkedin: apolloData.company_linkedin,
    city: apolloData.city,
    state: apolloData.state,
    country: apolloData.country,
    page_retrieved: apolloData.page_retrieved,

    // Instantly enrichment
    instantly_enrichment_status: instantlyData.status || 'not_enriched',
    instantly_campaign_data: instantlyData.campaign_data || {},

    // Combined enrichment metadata
    total_enrichment_score: (apolloData.page_retrieved || 100) + (instantlyData.status === 'enriched' ? 50 : 0),
    enrichment_timestamp: new Date().toISOString()
  };

  mergedData.push(mergedContact);
}

return mergedData;

// Prepare data for ML prediction
const predictionData = $input.all().map(contact => {
  const data = contact.json;

  // Create features matching the training model
  const features = {
    // Basic contact features
    has_first_name: data.first_name ? 1 : 0,
    has_last_name: data.last_name ? 1 : 0,
    has_title: data.title ? 1 : 0,
    has_company: data.company_name ? 1 : 0,

    // Company features
    company_size_numeric: data.company_size ? parseInt(data.company_size.replace(/[^0-9]/g, '')) : 0,
    company_age: data.company_founded_year ? (2025 - parseInt(data.company_founded_year)) : 0,
    has_website: data.company_website ? 1 : 0,
    has_linkedin: data.company_linkedin ? 1 : 0,

    // Location features
    has_city: data.city ? 1 : 0,
    has_state: data.state ? 1 : 0,
    has_country: data.country ? 1 : 0,

    // Enrichment features
    enrichment_completeness: data.total_enrichment_score / 150, // Normalize to 0-1
    page_retrieved_normalized: data.page_retrieved ? (100 - data.page_retrieved) / 100 : 0,

    // Text features
    title_length: data.title ? data.title.length : 0,
    company_name_length: data.company_name ? data.company_name.length : 0
  };

  return {
    contact_id: data.apollo_contact_id,
    email: data.email,
    name: `${data.first_name || ''} ${data.last_name || ''}`.trim(),
    title: data.title,
    company: data.company_name,
    features: features
  };
});

return {
  json: {
    prediction_data: predictionData,
    total_contacts: predictionData.length,
    feature_count: Object.keys(predictionData[0].features).length
  }
};

// Combine predictions with contact data and create CSV
const predictions = $input.first().json.predictions;
const probabilities = $input.first().json.probabilities;
const contactData = $input.first().json.contact_data;

const csvRows = [];

// Add header
csvRows.push('Contact ID,Email,Name,Title,Company,Predicted Engagement Level,Probability No Engagement,Probability Opened,Probability Engaged,Confidence Score');

// Add data rows
for (let i = 0; i < predictions.length; i++) {
  const contact = contactData[i];
  const prediction = predictions[i];
  const prob = probabilities[i];

  // Map prediction levels to readable text
  const engagementLevels = ['No Engagement', 'Opened', 'Engaged'];
  const predictedLevel = engagementLevels[prediction] || 'Unknown';

  // Calculate confidence score
  const confidenceScore = Math.max(...prob) * 100;

  const row = [
    contact.contact_id,
    contact.email,
    contact.name,
    contact.title,
    contact.company,
    predictedLevel,
    (prob[0] * 100).toFixed(2) + '%',
    (prob[1] * 100).toFixed(2) + '%',
    (prob[2] * 100).toFixed(2) + '%',
    confidenceScore.toFixed(2) + '%'
  ].join(',');

  csvRows.push(row);
}

// Create summary statistics
const totalContacts = predictions.length;
const engagementCounts = predictions.reduce((acc, pred) => {
  acc[pred] = (acc[pred] || 0) + 1;
  return acc;
}, {});

const summary = {
  total_contacts: totalContacts,
  predictions: {
    'no_engagement': engagementCounts[0] || 0,
    'opened': engagementCounts[1] || 0,
    'engaged': engagementCounts[2] || 0
  },
  csv_content: csvRows.join('\n'),
  timestamp: new Date().toISOString()
};

return {
  json: summary
};

// Log prediction results
const results = $input.first().json;

console.log('ðŸŽ¯ Lead Prediction Complete!');
console.log('ðŸ“‹ Prediction Summary:');
console.log(`  Total Contacts: ${results.total_contacts}`);
console.log(`  No Engagement: ${results.predictions.no_engagement} (${(results.predictions.no_engagement/results.total_contacts*100).toFixed(1)}%)`);
console.log(`  Opened: ${results.predictions.opened} (${(results.predictions.opened/results.total_contacts*100).toFixed(1)}%)`);
console.log(`  Engaged: ${results.predictions.engaged} (${(results.predictions.engaged/results.total_contacts*100).toFixed(1)}%)`);
console.log(`  CSV saved to: apollo_lead_predictions.csv`);

return {
  json: {
    status: 'success',
    message: 'Lead predictions completed successfully',
    results: results,
    timestamp: new Date().toISOString()
  }
};
